import styled, { css } from "styled-components/macro";

export const StyledButton = styled.button`
  color: black;
  background: white;
  font-size: 20px;
  border-radius: 5px;

  ${prop => 
    prop.primary && 
    css`
      color: green;
      background: grey;
    `
  };
`;

export const Container = styled.div`
padding-left: 0.75rem;
padding-right: 0.75rem;
  margin-left: auto;
  margin-right: auto;
  @media (min-width: 576px) {
    max-width: 540px;
  }
  @media (min-width: 768px) {
    max-width: 720px;
  }
  @media (min-width: 576px) {
    max-width: 540px;
  }
  @media (min-width: 576px) {
    max-width: 540px;
  }
  @media (min-width: 576px) {
    max-width: 540px;
  }
`;
